import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import config from "../../Common/Configurations/APIConfig";
import axios from "axios";

export default function LayoutSideBar() {
  const [notificationCount, setNotificationCount] = useState(0);
  const role = localStorage.getItem("roleName");

  const headerConfig = {
    headers: {
      Authorization: "Bearer " + localStorage.getItem("userToken"),
      "Content-Type": "application/json",
    },
  };

  const fetchNotificationCounts = async () => {
    try {
      console.log("Fetching notification counts...");
      const response = await axios.get(
        `${config.APIACTIVATEURL}${config.GETCOUNT}`,
        headerConfig
      );

      const data = response.data;
      console.log("API response:", data); // Debugging log
      if (data && data.totalCount !== undefined) {
        setNotificationCount(data.totalCount); // Update the notification count
        console.log("Updated notificationCount state:", data.totalCount); // Debugging log
      }
    } catch (error) {
      console.error("Error fetching notification counts:", error);
    }
  };

  useEffect(() => {
    fetchNotificationCounts();
    const interval = setInterval(fetchNotificationCounts, 60000); // Refresh every minute
    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  return (
    <div className="app-menu navbar-menu">
      <div className="navbar-brand-box">
        <Link to={"/"} className="logo logo-light">
          <span className="logo-lg" style={{ color: "#fff", fontSize: "20px" }}>
            CALENDAR
          </span>
        </Link>
        <button
          type="button"
          className="btn btn-sm p-0 fs-20 header-item float-end btn-vertical-sm-hover"
          id="vertical-hover"
        >
          <i className="ri-record-circle-line" />
        </button>
      </div>
      <div id="scrollbar">
        <div className="container-fluid">
          <div id="two-column-menu"></div>
          <ul className="navbar-nav" id="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link menu-link" to={"/dashboard"}>
                <i className="ri-store-line"></i>
                <span data-key="t-dashboards">Dashboard</span>
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link menu-link" to={"/notifications"} style={{ position: "relative" }}>
                <i className="ri-notification-3-line"></i>
                <span data-key="t-notifications">Notifications</span>
                {notificationCount > 0 && (
                  <span
                    className="badge bg-danger text-white"
                    style={{
                      position: "absolute",
                      top: "-5px", // Adjust top positioning
                      right: "-10px", // Adjust right positioning
                      padding: "5px 10px",
                      borderRadius: "50%",
                      fontSize: "12px",
                      zIndex: 10,
                    }}
                  >
                    {notificationCount}
                  </span>
                )}
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}
