import React, { useState } from "react";

export default function TestBadge() {
  const [notificationCount, setNotificationCount] = useState(5); // Set manually for testing

  return (
    <div>
      Notifications
      {notificationCount > 0 && (
        <span
          className="badge bg-danger text-white"
          style={{
            marginLeft: "10px",
            padding: "5px 10px",
            borderRadius: "10px",
            fontSize: "12px",
          }}
        >
          {notificationCount}
        </span>
      )}
    </div>
  );
}
