import React, { useEffect, useState } from "react";
import axios from "axios";
import config from "../../Common/Configurations/APIConfig";


const Notification = () => {
  const [notificationsData, setNotificationsData] = useState([]);
  const [overdueCount, setOverdueCount] = useState(0);
  const [dueTodayCount, setDueTodayCount] = useState(0);

  const headerconfig = {
    headers: {
      Authorization: "Bearer " + localStorage.getItem("userToken"),
      "Content-Type": "application/json",
    },
  };
  const apiConfig = {
    fetchNotifications: () =>
      axios.get(config.APIACTIVATEURL + config.GETNOTIFICATION, {
        ...headerconfig,
      }),
  };

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const response = await apiConfig.fetchNotifications();
        const data = response.data;

        // Calculate counts
        let overdueTotal = 0;
        let dueTodayTotal = 0;

        data.forEach((company) => {
          overdueTotal += company.Overdue.length;
          dueTodayTotal += company.DueToday.length;
        });

        setOverdueCount(overdueTotal);
        setDueTodayCount(dueTodayTotal);
        setNotificationsData(data);
      } catch (error) {
        console.error("Error fetching notifications data:", error);
      }
    };

    fetchNotifications();
  }, []);
  return (
    <div style={{ padding: "20px" }}>
      <h1>Notifications Dashboard</h1>
      <div style={{ display: "flex", gap: "20px", marginBottom: "20px" }}>
        {/* Overdue Notifications */}
        <div
          style={{
            flex: 1,
            backgroundColor: "#f8d7da",
            color: "#721c24",
            padding: "20px",
            borderRadius: "5px",
          }}
        >
          <h3>Overdue Notifications</h3>
          <h1>{overdueCount}</h1>
        </div>

        {/* Due Today Notifications */}
        <div
          style={{
            flex: 1,
            backgroundColor: "#fff3cd",
            color: "#856404",
            padding: "20px",
            borderRadius: "5px",
          }}
        >
          <h3>Due Today Notifications</h3>
          <h1>{dueTodayCount}</h1>
        </div>
      </div>

      {/* Notifications List */}
      <div>
        {notificationsData.map((company, index) => (
          <div key={index} style={{ marginBottom: "20px" }}>
            <h3 style={{ margin: "10px 0", color: "#0056b3" }}>
              {company.CompanyName}
            </h3>
            {/* Overdue Notifications */}
            {company.Overdue.length > 0 && (
              <div
                style={{
                  backgroundColor: "#f8d7da",
                  padding: "10px",
                  borderRadius: "5px",
                  marginBottom: "10px",
                }}
              >
                <h4>Overdue Notifications</h4>
                {company.Overdue.map((notification, idx) => (
                  <div
                    key={idx}
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      padding: "5px 0",
                      borderBottom: "1px solid #f5c6cb",
                    }}
                  >
                    <span>{notification.Description}</span>
                    <span>
                      Last notification:{" "}
                      {new Date(notification.ScheduledDate).toLocaleDateString()}
                    </span>
                    <span>{notification.DaysOverdue} days overdue</span>
                    <button
                      style={{
                        background: "none",
                        color: "#721c24",
                        border: "none",
                        cursor: "pointer",
                      }}
                    >
                      Disable Highlight
                    </button>
                  </div>
                ))}
              </div>
            )}

            {/* Due Today Notifications */}
            {company.DueToday.length > 0 && (
              <div
                style={{
                  backgroundColor: "#fff3cd",
                  padding: "10px",
                  borderRadius: "5px",
                  marginBottom: "10px",
                }}
              >
                <h4>Due Today Notifications</h4>
                {company.DueToday.map((notification, idx) => (
                  <div
                    key={idx}
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      padding: "5px 0",
                      borderBottom: "1px solid #ffeeba",
                    }}
                  >
                    <span>{notification.Description}</span>
                    <span>
                      Scheduled date:{" "}
                      {new Date(notification.ScheduledDate).toLocaleDateString()}
                    </span>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Notification;
