import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import config from "../../Common/Configurations/APIConfig";
import { handleSuccess, handleError } from "../../Common/Layout/CustomAlerts";
import Flatpickr from "react-flatpickr"; // Import Flatpickr
import "flatpickr/dist/themes/material_blue.css"; // Import Flatpickr theme


const initialFieldValues = {
    companyId: "00000000-0000-0000-0000-000000000000",
    companyName: "",
    location: "",
    linkedInProfile: "",
    emails: "",
    phoneNumbers: "",
    comments: "",
    communicationPeriodicity: "",
};

export default function Dashboard() {
    const [lists, setLists] = useState([]);
    const [values, setValues] = useState(initialFieldValues);
    const [recordForEdit, setRecordForEdit] = useState(null);
    const [errors, setErrors] = useState({});
    const [btnSubmit, setBtnSubmit] = useState(true);
    const [totalPages, setTotalPages] = useState(0);
    const [pageSize, setPageSize] = useState(20);
    const [pageNumber, setPageNumber] = useState(1);
    const [data, setData] = useState([]);
    const [totalRecords, setTotalRecords] = useState(0);
    const [showModal, setShowModal] = useState(false);
    const [selectedCommunication, setSelectedCommunication] = useState("");
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [notes, setNotes] = useState("");
    const [selectedCompany, setSelectedCompany] = useState(null); // Track the selected company
    const [selectedCompanies, setSelectedCompanies] = useState([]);

    const headerConfig = {
        headers: {
            Authorization: "Bearer " + localStorage.getItem("userToken"),
            "Content-Type": "application/json",
        },
    };

    useEffect(() => {
        if (recordForEdit !== null) {
            setValues(recordForEdit);
        }
    }, [recordForEdit]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setValues({
            ...values,
            [name]: value,
        });
    };

    const validate = () => {
        let temp = {};
        temp.companyName = values.companyName !== "";
        temp.location = values.location !== "";
        temp.linkedInProfile = values.linkedInProfile !== "";
        temp.emails = values.emails !== "";
        temp.phoneNumbers = values.phoneNumbers !== "";
        temp.comments = values.comments !== "";
        temp.communicationPeriodicity = values.communicationPeriodicity !== "";
        setErrors(temp);
        return Object.values(temp).every((x) => x === true);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validate()) {
            setBtnSubmit(false);
            const payload = { ...values };
            addOrEdit(payload);
        }
    };
    const handleCommunicationPerformed = () => {
        console.log('Selected companies:', selectedCompanies);
        if (selectedCompanies.length > 0) {
          // Open modal with the selected company IDs
          setShowModal(true);  // Open the modal
          console.log("ShowModal set to true");
        } else {
          alert('Please select at least one company!');
        }
      };
    
      const handleCloseModal = () => {
        setShowModal(false);
        setSelectedCompanies([]);  // Reset selected companies when closing modal
      };
      const handleModalSubmit = () => {
        const payload = {
            selectedCompanies,
            selectedCommunication,
            selectedDate,
            notes,
        };
        console.log('Submitting payload:', payload);
        // Call your API to submit the data
        setShowModal(false);
    };
    
    
    const handleCheckboxChange = (companyId) => {
        setSelectedCompanies((prevSelectedCompanies) => {
          if (prevSelectedCompanies.includes(companyId)) {
            // Deselect company
            return prevSelectedCompanies.filter((id) => id !== companyId);
          } else {
            // Select company
            return [...prevSelectedCompanies, companyId];
          }
        });
      };

  // Open the modal
  const openModal = () => {
    setShowModal(true);
  };

  // Close the modal
 
    const isToday = (date) => {
        const today = new Date();
        return (
            date.getDate() === today.getDate() &&
            date.getMonth() === today.getMonth() &&
            date.getFullYear() === today.getFullYear()
        );
    };
    const isPast = (date) => date < new Date();

    const applicationAPI = () => {
        return {
            create: (newRecord) =>
                axios.post(
                    `${config.APIACTIVATEURL}${config.CREATECOMPANYMANAGEMENT}`,
                    newRecord,
                    headerConfig
                ),
            update: (updateRecord) =>
                axios.put(
                    `${config.APIACTIVATEURL}${config.UPDATECOMPANYMANAGEMENT}`,
                    updateRecord,
                    headerConfig
                ),
        };
    };

    const addOrEdit = (payload) => {
        if (payload.companyId === initialFieldValues.companyId) {
            applicationAPI()
                .create(payload)
                .then((res) => {
                    if (res.data.statusCode === 200) {
                        handleSuccess("Record Created");
                        resetForm();
                        getData(pageNumber);
                    } else {
                        handleError(res.data.message);
                    }
                })
                .catch(() => {
                    handleError("Error in saving data");
                })
                .finally(() => setBtnSubmit(true));
        } else {
            applicationAPI()
                .update(payload)
                .then((res) => {
                    if (res.data.statusCode === 200) {
                        handleSuccess("Record Updated");
                        resetForm();
                        getData(pageNumber);
                    } else {
                        handleError(res.data.message);
                    }
                })
                .catch(() => {
                    handleError("Error in updating data");
                })
                .finally(() => setBtnSubmit(true));
        }
    };

    const resetForm = () => {
        setValues(initialFieldValues);
        setErrors({});
    };

    const showEditDetails = (data) => {
        setRecordForEdit(data);
    };

    const getData = (number) => {
        axios
            .get(
                `${config.APIACTIVATEURL}${config.GETCOMMUNICATION}?pageNumber=${number}&pageSize=${pageSize}`,
                headerConfig
            )
            .then((response) => {
                setLists(response.data.data);
                setPageNumber(response.data.data.pageNumber);
                setPageSize(response.data.data.pageSize);
                setTotalPages(response.data.data.totalPages);
                setData(response.data.data);
                setTotalRecords(response.data.data.totalRecords);
            });
    };

    useEffect(() => {
        getData(pageNumber);
    }, []);

    const renderPageNumbers = [...Array(totalPages)].map((_, index) => {
        const page = index + 1;
        return (
            <li
                className="page-item"
                key={page}
                onClick={() => getData(page)}
            >
                <Link className="page-link">{page}</Link>
            </li>
        );
    });

    return (
        <div className="row">
            <div className="col-xl-12">
                <div className="card card-height-100">
                    <div className="card-header align-items-center d-flex">
                        <h4 className="card-title mb-0 flex-grow-1">Communications</h4>
                        {/* Add the button */}
                        {/* <button
    className="btn btn-primary ms-auto"
    onClick={() => {
        console.log('Communication Performed clicked');
        handleCommunicationPerformed();
    }}
    disabled={selectedCompanies.length === 0} // Disable button if no company is selected
>
    Communication Performed
</button> */}
<button
                className="btn btn-primary ms-auto"
                onClick={handleCommunicationPerformed}
                disabled={selectedCompanies.length === 0} // Disable button if no company is selected
              >
                Communication Performed
              </button>

                    </div>
                  {/* Modal */}
{showModal && (
    <div
        style={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            background: 'white',
            border: '1px solid #ccc',
            borderRadius: '8px',
            width: '400px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
            zIndex: 1000,
            padding: '20px',
        }}
    >
        <div>
            <h5>Communication Performed</h5>
            <hr />
        </div>
        <div style={{ marginBottom: '20px' }}>
            {/* Dropdown for Communication */}
            <div style={{ marginBottom: '10px' }}>
                <label htmlFor="communicationSelect" style={{ fontWeight: 'bold' }}>Communication:</label>
                <select
                    id="communicationSelect"
                    style={{
                        width: '100%',
                        padding: '8px',
                        marginTop: '5px',
                        border: '1px solid #ccc',
                        borderRadius: '4px',
                    }}
                    value={selectedCommunication}
                    onChange={(e) => setSelectedCommunication(e.target.value)}
                >
                    <option value="">Select Communication</option>
                    {communications.map((comm) => (
                        <option key={comm.communicationId} value={comm.communicationId}>
                            {comm.communicationName}
                        </option>
                    ))}
                </select>
            </div>

            {/* Flatpickr for Date */}
            <div style={{ marginBottom: '10px' }}>
                <label htmlFor="updatedDate" style={{ fontWeight: 'bold' }}>Updated Date:</label>
                <Flatpickr
                    id="updatedDate"
                    value={selectedDate}
                    onChange={(date) => setSelectedDate(date)}
                    options={{ dateFormat: 'Y-m-d' }}
                    style={{
                        width: '100%',
                        padding: '8px',
                        marginTop: '5px',
                        border: '1px solid #ccc',
                        borderRadius: '4px',
                    }}
                />
            </div>

            {/* Notes Textarea */}
            <div>
                <label htmlFor="notes" style={{ fontWeight: 'bold' }}>Add Notes:</label>
                <textarea
                    id="notes"
                    value={notes}
                    onChange={(e) => setNotes(e.target.value)}
                    rows="4"
                    style={{
                        width: '100%',
                        padding: '8px',
                        marginTop: '5px',
                        border: '1px solid #ccc',
                        borderRadius: '4px',
                    }}
                ></textarea>
            </div>
        </div>

        {/* Footer Buttons */}
        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
            <button
                style={{
                    padding: '8px 16px',
                    background: '#ccc',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                }}
                onClick={handleCloseModal}
            >
                Cancel
            </button>
            <button
                style={{
                    padding: '8px 16px',
                    background: '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                }}
                onClick={handleModalSubmit}
            >
                Submit
            </button>
        </div>
    </div>
)}

  
      <div className="card-body">
                        <div className="table-responsive table-card">
                            <table className="table align-middle">
                                <thead className="table-light">
                                    <tr>
                                        <th>S.No</th>
                                        <th>Select</th>
                                        <th>Company Name</th>
                                        <th>Last Communication</th>
                                        <th>Next Communication</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {lists.length > 0 ? (
                                        lists.map((company, index) => {
                                            // Extract communications for the company
                                            const lastCommunications = company.communications.filter(
                                                (c) => c.status === true
                                            );
                                            const nextCommunications = company.communications.find(
                                                (c) => c.status === false
                                            );

                                            return (
                                                <tr key={company.companyId}>
                                                    <td>{index + 1}</td>
                                                    <td>
                                                    <input
                                                            type="checkbox"
                                                            checked={selectedCompanies.includes(company.companyId)}
                                                            onChange={() => handleCheckboxChange(company.companyId)}
                                                        />
                                                    </td>
                                                    <td>{company.companyName}</td>
                                                    <td>
                                                        {lastCommunications.length > 0 ? (
                                                            <>
                                                                {lastCommunications.map((communication, idx) => (
                                                                    <div key={idx}>
                                                                        {communication.communicationName} -{" "}
                                                                        {new Date(
                                                                            communication.scheduledDate
                                                                        ).toLocaleDateString()}
                                                                    </div>
                                                                ))}
                                                            </>
                                                        ) : (
                                                            "No Completed Communications"
                                                        )}
                                                    </td>
                                                    <td>
                                                        {nextCommunications ? (
                                                            <>
                                                                <span
                                                                    style={{
                                                                        color: isToday(new Date(nextCommunications.scheduledDate))
                                                                            ? "yellow"
                                                                            : isPast(new Date(nextCommunications.scheduledDate))
                                                                                ? "red"
                                                                                : "inherit",
                                                                        fontWeight: "bold", // Make the text bold
                                                                    }}
                                                                >
                                                                    {nextCommunications.communicationName}
                                                                </span>{" "}
                                                                -{" "}
                                                                {new Date(nextCommunications.scheduledDate).toLocaleDateString()}
                                                                <br />
                                                                <button className="btn btn-link text-danger">
                                                                    Disable Highlight
                                                                </button>
                                                            </>
                                                        ) : (
                                                            "No Upcoming Communication"
                                                        )}
                                                    </td>



                                                </tr>
                                            );
                                        })
                                    ) : (
                                        <tr>
                                            <td colSpan="6" className="text-center">
                                                No data available
                                            </td>
                                        </tr>
                                    )}
                                </tbody>



                            </table>
                        </div>
                        <div className="align-items-center mt-4 pt-2 justify-content-between d-flex">
                            <div className="flex-shrink-0">
                                <div className="text-muted">
                                    Showing{" "}
                                    <span className="fw-semibold">{lists.length}</span> of{" "}
                                    <span className="fw-semibold">{totalRecords}</span>{" "}
                                    Results
                                </div>
                            </div>
                            {/* <ul className="pagination pagination-separated pagination-sm mb-0">
                       <li
                         className={
                           "page-item" + data.previousPage === null
                             ? "disabled"
                             : ""
                         }
                         onClick={() => GetFirstPageData()}
                       >
                         <Link className="page-link">Previous</Link>
                       </li>
                       {renderPageNumbers}
                       <li
                         className={
                           "page-item" + data.nextPage === null
                             ? "disabled"
                             : ""
                         }
                         onClick={() => GetLastPageData()}
                       >
                         <Link className="page-link">Next</Link>
                       </li>
                     </ul> */}
                        </div>
                    </div>
                </div>
            </div>
            


        </div>
        

    );
}
