import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import config from "../../Common/Configurations/APIConfig";
import { handleSuccess, handleError } from "../../Common/Layout/CustomAlerts";
import LayoutHeader from "../../Common/Layout/Header";
import LayoutSideBar from "../../Common/Layout/Sidebar";

const initialFieldValues = {
 companyId: "00000000-0000-0000-0000-000000000000",
 companyName: "",
 location: "",
 linkedInProfile: "",
 emails: "",
 phoneNumbers: "",
 comments: "",
 communicationPeriodicity: ""
};
export default function Dashboard() {
 const [lists, setLists] = useState([]);
 const [values, setValues] = useState(initialFieldValues);
 const [recordForEdit, setRecordForEdit] = useState(null);
 const [errors, setErrors] = useState({});
 const [btnSubmit, setBtnSubmit] = useState(true);
 const [totalPages, setTotalPages] = useState(0);
 const [pageSize, setPageSize] = useState(20);
 const [pageNumber, setPageNumber] = useState(1);
 const [data, setData] = useState([]);
 const [totalRecords, setTotalRecords] = useState(0);
 const [selectedClient, setSelectedClient] = useState(null);
 const [emailFields, setEmailFields] = useState([""]); // Manage dynamic email inputs
 const [phoneFields, setPhoneFields] = useState([""]); // Manage dynamic phone inputs
 
 
 const headerconfig = {
   headers: {
     Authorization: "Bearer " + localStorage.getItem("userToken"),
     "Content-Type": "application/json", // For JSON requests
   },
 };
 
 useEffect(() => {
   if (recordForEdit !== null) {
     setValues(recordForEdit);
   }
 }, [recordForEdit]);

 const handleInputChange = (e) => {
   const { name, value } = e.target;
   setValues({
     ...values,
     [name]: value,
   });
 };
 const validate = () => {
   let temp = {};
   temp.companyName = values.companyName === "" ? false : true;
   temp.location = values.location === "" ? false : true;
   temp.linkedInProfile = values.linkedInProfile === "" ? false : true;
   temp.emails = values.emails === "" ? false : true;
   temp.phoneNumbers = values.phoneNumbers === "" ? false : true;
   temp.comments = values.comments === "" ? false : true;
   temp.communicationPeriodicity = values.communicationPeriodicity === "" ? false : true;
   setErrors(temp);
   return Object.values(temp).every((x) => x === true);
 };

 const handleSubmit = (e) => {
   e.preventDefault();
   if (validate()) {
     setBtnSubmit(false);
     const payload = {
       companyId: values.companyId,
       companyName: values.companyName,
       location: values.location, // Corrected typo here
       linkedInProfile: values.linkedInProfile,
       emails: values.emails,
       phoneNumbers: values.phoneNumbers,
       comments: values.comments,
       communicationPeriodicity: values.communicationPeriodicity,
     };
     addOrEdit(payload);
   }
 };
 
 const applicationAPI = () => {
   return {
     create: (newrecord) =>
       axios.post(config.APIACTIVATEURL + config.CREATECOMPANYMANAGEMENT, newrecord, {
         ...headerconfig,
       }),
     update: (updateRecord) =>
       axios.put(config.APIACTIVATEURL + config.UPDATECOMPANYMANAGEMENT, updateRecord, {
         ...headerconfig,
       }),
   };
 };
 const addOrEdit = (payload) => {
   if (payload.companyId === "00000000-0000-0000-0000-000000000000") {
     applicationAPI()
       .create(payload)  // Send as JSON, not FormData
       .then((res) => {
         if (res.data.statusCode === 200) {
           handleSuccess("Record Created");
           resetForm();
           GetData(pageNumber);
           setBtnSubmit(true);
         } else {
           handleError(res.data.message);
           setBtnSubmit(true);
         }
       })
       .catch(function (error) {
         handleError("Error in saving data");
         setBtnSubmit(true);
       });
   } else {
     applicationAPI()
       .update(payload)  // Send as JSON, not FormData
       .then((res) => {
         if (res.data.statusCode === 200) {
           handleSuccess("Record Updated");
           resetForm();
           GetData(pageNumber);
           setBtnSubmit(true);
         } else {
           handleError(res.data.message);
           setBtnSubmit(true);
         }
       })
       .catch(function (error) {
         handleError("Error in updating data");
         setBtnSubmit(true);
       });
   }
 };
 
 const resetForm = () => {
   setValues(initialFieldValues);
   setErrors({});
 };
 const showEditDetails = (data) => {
   setRecordForEdit(data);
 };
 const GetLastPageData = () => {
   GetData(totalPages);
 };
 const GetFirstPageData = () => {
   GetData("1");
 };
 const GetPageData = (number) => {
   setPageNumber(number);
   if (pageNumber !== number) GetData(number);
 };
 const pageNumbers = [];
 for (let i = 1; i <= totalPages; i++) {
   pageNumbers.push(i);
 }
 const renderPageNumbers = pageNumbers.map((number) => {
   return (
     <li
       className="page-item"
       key={number}
       id={number}
       onClick={() => GetPageData(number)}
     >
       <Link className="page-link">{number}</Link>
     </li>
   );
 });
 
 const onDelete = (e, id) => {
   if (window.confirm("Are you sure to delete this record?")) {
     axios
       .delete(config.APIACTIVATEURL + config.DELETECOMPANYMANAGEMENT + "/" + id, {
         ...headerconfig,
       })
       .then((response) => {
         if (response.data.statusCode === 200) {
           handleSuccess(response.data.data);
           GetData(pageNumber);
           resetForm();
         } else {
           handleError(response.data.data);
           GetData(pageNumber);
           resetForm();
         }
       });
   }
 };
 const GetData = (number) => {
   axios
     .get(
       config.APIACTIVATEURL +
         config.GETCOMMUNICATION +
         "?pageNumber=" +
         number +
         "&pageSize=" +
         pageSize,
       { ...headerconfig }
     )
     .then((response) => {
       setLists(response.data.data);
       setPageNumber(response.data.data.pageNumber);
       setPageSize(response.data.data.pageSize);
       setTotalPages(response.data.data.totalPages);
       setData(response.data.data);
       setTotalRecords(response.data.data.totalRecords);
     });
 };
 const applyErrorClass = (field) =>
   field in errors && errors[field] === false ? " form-control-danger" : "";
 useEffect(() => {
   GetData(pageNumber);
 }, []);
 

return (
    <div className="row">
      <div className="col-xl-12">
        <div className="card card-height-100">
          <div className="card-header align-items-center d-flex">
            <h4 className="card-title mb-0 flex-grow-1">Communications</h4>
          </div>
          <div className="card-body">
            <div className="table-responsive table-card">
              <table className="table align-middle">
                <thead className="table-light">
                  <tr>
                    <th>S.No</th>
                    <th>Select</th>
                    <th>Company Name</th>
                    <th>Last Communication</th>
                    <th>Next Communication</th>
                    <th>Highlight</th>
                  </tr>
                </thead>
                <tbody>
                  {communications.length > 0 ? (
                    communications.map((company, index) => {
                      // Extract communications for the company
                      const lastCommunication = company.communications.slice(-1)[0];
                      const nextCommunication =
                        company.communications.find((c) => new Date(c.scheduledDate) > new Date()) || null;

                      return (
                        <tr key={company.companyId}>
                          <td>{index + 1}</td>
                          <td>
                            <input type="checkbox" />
                          </td>
                          <td>{company.companyName}</td>
                          <td>
                            {lastCommunication ? (
                              <>
                                {lastCommunication.communicationName} -{" "}
                                {new Date(lastCommunication.scheduledDate).toLocaleDateString()}
                              </>
                            ) : (
                              "No Communications"
                            )}
                          </td>
                          <td>
                            {nextCommunication ? (
                              <>
                                {nextCommunication.communicationName} -{" "}
                                {new Date(nextCommunication.scheduledDate).toLocaleDateString()}
                              </>
                            ) : (
                              "No Upcoming Communication"
                            )}
                          </td>
                          <td>
                            <button className="btn btn-link text-danger">
                              Disable Highlight
                            </button>
                          </td>
                        </tr>
                      );
                    })
                  ) : (
                    <tr>
                      <td colSpan="6" className="text-center">
                        No data available
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
                   <div className="align-items-center mt-4 pt-2 justify-content-between d-flex">
                     <div className="flex-shrink-0">
                       <div className="text-muted">
                         Showing{" "}
                         <span className="fw-semibold">{lists.length}</span> of{" "}
                         <span className="fw-semibold">{totalRecords}</span>{" "}
                         Results
                       </div>
                     </div>
                     <ul className="pagination pagination-separated pagination-sm mb-0">
                       <li
                         className={
                           "page-item" + data.previousPage === null
                             ? "disabled"
                             : ""
                         }
                         onClick={() => GetFirstPageData()}
                       >
                         <Link className="page-link">Previous</Link>
                       </li>
                       {renderPageNumbers}
                       <li
                         className={
                           "page-item" + data.nextPage === null
                             ? "disabled"
                             : ""
                         }
                         onClick={() => GetLastPageData()}
                       >
                         <Link className="page-link">Next</Link>
                       </li>
                     </ul>
                   </div>
                 </div>
               </div>
             </div>
           </div>
           
         
 );
}
