import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function LayoutSideBar() {
  // Commenting out the state and dynamic logic temporarily
  // const [notificationCount, setNotificationCount] = useState(0);

  // Hardcoding a test value for manual debugging
  const notificationCount = 5; // Replace with any number to test

  return (
    <div className="app-menu navbar-menu">
      <div className="navbar-brand-box">
        <Link to={"/"} className="logo logo-light">
          <span className="logo-lg" style={{ color: "#fff", fontSize: "20px" }}>
            CALENDAR
          </span>
        </Link>
        <button
          type="button"
          className="btn btn-sm p-0 fs-20 header-item float-end btn-vertical-sm-hover"
          id="vertical-hover"
        >
          <i className="ri-record-circle-line" />
        </button>
      </div>
      <div id="scrollbar">
        <div className="container-fluid">
          <ul className="navbar-nav" id="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link menu-link" to={"/dashboard"}>
                <i className="ri-store-line"></i>
                <span>Dashboard</span>
              </Link>
            </li>
            <li className="nav-item">
              <Link
                className="nav-link menu-link"
                to={"/notifications"}
                style={{ position: "relative" }}
              >
                <i className="ri-notification-3-line"></i>
                <span>Notifications</span>
                {/* Hardcoded value for testing */}
                {notificationCount > 0 && (
                  <span
                    className="badge bg-danger text-white"
                    style={{
                      position: "absolute",
                      top: "-5px",
                      right: "-10px",
                      padding: "5px 10px",
                      borderRadius: "50%",
                      fontSize: "12px",
                      zIndex: 10,
                    }}
                  >
                    {notificationCount}
                  </span>
                )}
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}
