import React, { useEffect, useState } from "react";
import axios from "axios";
import config from "../../Common/Configurations/APIConfig";



const Notification = () => {
  const [notificationsData, setNotificationsData] = useState([]);
  const [overdueTotal, setOverdueTotal] = useState(0);
  const [dueTodayTotal, setDueTodayTotal] = useState(0);

  const apiConfig = {
    fetchNotifications: () =>
      axios.get(config.APIACTIVATEURL + config.GETNOTIFICATION, {
        ...headerconfig,
      }),
  };
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await apiConfig.fetchNotifications();
        const data = response.data;
  
        let overdueTotal = 0;
        let dueTodayTotal = 0;
  
        data.forEach((company) => {
          overdueTotal += company.overdue.length; // Changed from Overdue to overdue
          dueTodayTotal += company.dueToday.length; // Changed from DueToday to dueToday
        });
  
        setOverdueTotal(overdueTotal);
        setDueTodayTotal(dueTodayTotal);
        setNotificationsData(data);
      } catch (error) {
        console.error("Error fetching notifications:", error);
      }
    };
  
    fetchData();
  }, []);
    

  return (
    <div>
      <h1>Notifications Dashboard</h1>
      <div style={{ marginBottom: "20px" }}>
        <strong>Total Overdue: </strong>
        {overdueTotal}
      </div>
      <div style={{ marginBottom: "20px" }}>
        <strong>Total Due Today: </strong>
        {dueTodayTotal}
      </div>

      {notificationsData.map((company, index) => (
        <div key={index} style={{ marginBottom: "20px" }}>
          <h3 style={{ margin: "10px 0", color: "#0056b3" }}>
            {company.companyName} {/* Changed from CompanyName to companyName */}
          </h3>

          {/* Overdue Notifications */}
          {company.overdue.length > 0 && (
            <div
              style={{
                backgroundColor: "#f8d7da",
                padding: "10px",
                borderRadius: "5px",
                marginBottom: "10px",
              }}
            >
              <h4>Overdue Notifications</h4>
              {company.overdue.map((notification, idx) => (
                <div
                  key={idx}
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "5px 0",
                    borderBottom: "1px solid #f5c6cb",
                  }}
                >
                  <span>{notification.description}</span> {/* Changed from Description to description */}
                  <span>
                    Last notification:{" "}
                    {new Date(notification.scheduledDate).toLocaleDateString()}{" "}
                    {/* Changed from ScheduledDate to scheduledDate */}
                  </span>
                  <span>{notification.daysOverdue} days overdue</span>{" "}
                  {/* Changed from DaysOverdue to daysOverdue */}
                  <button
                    style={{
                      background: "none",
                      color: "#721c24",
                      border: "none",
                      cursor: "pointer",
                    }}
                  >
                    Disable Highlight
                  </button>
                </div>
              ))}
            </div>
          )}

          {/* Due Today Notifications */}
          {company.dueToday.length > 0 && (
            <div
              style={{
                backgroundColor: "#fff3cd",
                padding: "10px",
                borderRadius: "5px",
                marginBottom: "10px",
              }}
            >
              <h4>Due Today Notifications</h4>
              {company.dueToday.map((notification, idx) => (
                <div
                  key={idx}
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "5px 0",
                    borderBottom: "1px solid #ffeeba",
                  }}
                >
                  <span>{notification.description}</span> {/* Changed from Description to description */}
                  <span>
                    Scheduled date:{" "}
                    {new Date(notification.scheduledDate).toLocaleDateString()}{" "}
                    {/* Changed from ScheduledDate to scheduledDate */}
                  </span>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default NotificationsComponent;
