import React, { useEffect, useState } from "react";
import axios from "axios";
import { format, parseISO } from "date-fns";
import config from "../../Common/Configurations/APIConfig";

const Calendar = () => {
  const [communications, setCommunications] = useState([]);
  const [currentMonth, setCurrentMonth] = useState(new Date());

  const headerconfig = {
    headers: {
      Authorization: "Bearer " + localStorage.getItem("userToken"),
      "Content-Type": "application/json",
    },
  };

  const apiConfig = {
    fetchCalendar: () =>
      axios.get(config.APIACTIVATEURL + config.GETCALENDAR, {
        ...headerconfig,
      }),
  };

  useEffect(() => {
    apiConfig
      .fetchCalendar()
      .then((response) => setCommunications(response.data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    return new Date(year, month + 1, 0).getDate();
  };

  const renderCalendarDays = () => {
    const daysInMonth = getDaysInMonth(currentMonth);
    const firstDayOfMonth = new Date(
      currentMonth.getFullYear(),
      currentMonth.getMonth(),
      1
    ).getDay();

    const days = [];
    for (let i = 0; i < firstDayOfMonth; i++) {
      days.push(<div key={`empty-${i}`} className="calendar-day empty"></div>);
    }

    for (let i = 1; i <= daysInMonth; i++) {
      const currentDate = new Date(
        currentMonth.getFullYear(),
        currentMonth.getMonth(),
        i
      );
      const formattedDate = format(currentDate, "yyyy-MM-dd");

      const dailyEvents = communications.filter((comm) =>
        comm.scheduledDate.startsWith(formattedDate)
      );

      days.push(
        <div key={i} className="calendar-day">
          <div className="date">{i}</div>
          {dailyEvents.length > 0 && (
            <div className="events">
              {dailyEvents.map((event, index) => (
                <div key={index} className="event">
                  {event.companyName} - {event.communicationType}
                </div>
              ))}
            </div>
          )}
        </div>
      );
    }

    return days;
  };

  const handlePrevMonth = () => {
    const newDate = new Date(
      currentMonth.getFullYear(),
      currentMonth.getMonth() - 1,
      1
    );
    setCurrentMonth(newDate);
  };

  const handleNextMonth = () => {
    const newDate = new Date(
      currentMonth.getFullYear(),
      currentMonth.getMonth() + 1,
      1
    );
    setCurrentMonth(newDate);
  };

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button onClick={handlePrevMonth}>{"<"}</button>
        <h2>{format(currentMonth, "MMMM yyyy")}</h2>
        <button onClick={handleNextMonth}>{">"}</button>
      </div>
      <div className="calendar-grid">{renderCalendarDays()}</div>
    </div>
  );
};

export default Calendar;



<style>
{`
.calendar-container {
  font-family: Arial, sans-serif;
  width: 90%;
  margin: 20px auto;
}

.calendar-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}

.calendar-header h2 {
  font-size: 24px;
}

.calendar-grid {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
}

.calendar-day {
  border: 1px solid #ddd;
  padding: 10px;
  background: #fff;
  min-height: 80px;
  display: flex;
  flex-direction: column;
}

.calendar-day.empty {
  background: transparent;
  border: none;
}

.calendar-day .date {
  font-size: 16px;
  font-weight: bold;
  margin-bottom: 5px;
}

.calendar-day .events {
  margin-top: 5px;
}

.event {
  background: #e3f2fd;
  padding: 5px;
  margin: 2px 0;
  border-radius: 4px;
  font-size: 12px;
  text-align: center;
}

`}
</style>
