import React, { useEffect, useState } from "react";
import axios from "axios";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import config from "../../Common/Configurations/APIConfig";
import LayoutHeader from "../../Common/Layout/Header";
import LayoutSideBar from "../../Common/Layout/Sidebar";

// Set up moment localizer for react-big-calendar
const localizer = momentLocalizer(moment);

const MainCalendar = () => {
  const [communications, setCommunications] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());

  const headerconfig = {
    headers: {
      Authorization: "Bearer " + localStorage.getItem("userToken"),
      "Content-Type": "application/json",
    },
  };

  const apiConfig = {
    fetchCalendar: () =>
      axios.get(config.APIACTIVATEURL + config.GETCALENDAR, {
        ...headerconfig,
      }),
  };

  useEffect(() => {
    apiConfig
      .fetchCalendar()
      .then((response) => setCommunications(response.data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  // Transform API data into events for react-big-calendar
  const events = communications.map((comm) => ({
    title: `${comm.companyName} - ${comm.communicationType}`,
    start: new Date(comm.scheduledDate),
    end: new Date(comm.scheduledDate), // Assuming single-day events
  }));

  const handleYearChange = (e) => {
    const newYear = parseInt(e.target.value, 10);
    const updatedDate = new Date(currentDate);
    updatedDate.setFullYear(newYear);
    setCurrentDate(updatedDate);
  };

  const handleMonthChange = (e) => {
    const newMonth = parseInt(e.target.value, 10);
    const updatedDate = new Date(currentDate);
    updatedDate.setMonth(newMonth);
    setCurrentDate(updatedDate);
  };

  const handleNavigate = (direction) => {
    const updatedDate = new Date(currentDate);
    if (direction === "next") {
      updatedDate.setMonth(currentDate.getMonth() + 1);
    } else if (direction === "back") {
      updatedDate.setMonth(currentDate.getMonth() - 1);
    }
    setCurrentDate(updatedDate);
  };

  return (
    <div id="layout-wrapper" style={{ backgroundColor: "#F08080" }}>
      <LayoutHeader />
      <LayoutSideBar />
      <div style={{ padding: "120px 50px 0 50px" }}>
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "20px",
            backgroundColor: "#FFF",
            padding: "10px",
            borderRadius: "8px",
          }}
        >
          <button
            onClick={() => handleNavigate("back")}
            style={{
              backgroundColor: "#FF6347",
              color: "white",
              border: "none",
              padding: "8px 16px",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Back
          </button>
          <div style={{ display: "flex", gap: "10px" }}>
            <label>
              Year:
              <select
                value={currentDate.getFullYear()}
                onChange={handleYearChange}
                style={{
                  padding: "5px",
                  borderRadius: "4px",
                  fontSize: "14px",
                }}
              >
                {Array.from({ length: 10 }, (_, i) => (
                  <option key={i} value={currentDate.getFullYear() - 5 + i}>
                    {currentDate.getFullYear() - 5 + i}
                  </option>
                ))}
              </select>
            </label>
            <label>
              Month:
              <select
                value={currentDate.getMonth()}
                onChange={handleMonthChange}
                style={{
                  padding: "5px",
                  borderRadius: "4px",
                  fontSize: "14px",
                }}
              >
                {moment.months().map((month, index) => (
                  <option key={index} value={index}>
                    {month}
                  </option>
                ))}
              </select>
            </label>
          </div>
          <button
            onClick={() => handleNavigate("next")}
            style={{
              backgroundColor: "#FF6347",
              color: "white",
              border: "none",
              padding: "8px 16px",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Next
          </button>
        </div>
        <div
          style={{
            height: "20vh",
            margin: "20px",
            borderRadius: "8px",
            backgroundColor: "#FFF",
            boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
            padding: "20px",
          }}
        >
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            style={{
              height: "100%",
              backgroundColor: "#FFFFFF",
              borderRadius: "8px",
              boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
            }}
            views={["month", "week", "day", "agenda"]}
            defaultView="month"
            popup
            selectable
            date={currentDate}
            onNavigate={(date) => setCurrentDate(date)}
          />
        </div>
      </div>
    </div>
  );
};

export default MainCalendar;
