import React, { useEffect, useState } from "react";
import axios from "axios";
import config from "../../Common/Configurations/APIConfig";
import { handleSuccess, handleError } from "../../Common/Layout/CustomAlerts";
import LayoutHeader from "../../Common/Layout/Header";
import LayoutSideBar from "../../Common/Layout/Sidebar";


const Notification = () => {
  const [notificationsData, setNotificationsData] = useState([]);
  const [overdueTotal, setOverdueTotal] = useState(0);
  const [dueTodayTotal, setDueTodayTotal] = useState(0);

  const headerconfig = {
    headers: {
      Authorization: "Bearer " + localStorage.getItem("userToken"),
      "Content-Type": "application/json",
    },
  };

  const apiConfig = {
    fetchNotifications: () =>
      axios.get(config.APIACTIVATEURL + config.GETNOTIFICATION, {
        ...headerconfig,
      }),
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await apiConfig.fetchNotifications();
        const data = response.data;

        let overdueTotal = 0;
        let dueTodayTotal = 0;

        data.forEach((company) => {
          overdueTotal += company.overdue.length;
          dueTodayTotal += company.dueToday.length;
        });

        setOverdueTotal(overdueTotal);
        setDueTodayTotal(dueTodayTotal);
        setNotificationsData(data);
      } catch (error) {
        console.error("Error fetching notifications:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div id="layout-wrapper">
          <LayoutHeader />
          <LayoutSideBar />
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        padding: "20px",
        backgroundColor: "#f9f9f9",
      }}
    >
      <h1 style={{ fontSize: "28px", marginBottom: "20px", color: "#333" }}>
        Notifications Dashboard
      </h1>

      <div
        style={{
          display: "flex",
          gap: "20px",
          marginBottom: "20px",
          flexWrap: "wrap",
        }}
      >
        {/* Overdue Card */}
        <div
          style={{
            flex: "1",
            backgroundColor: "#fdecea",
            borderRadius: "10px",
            padding: "20px",
            textAlign: "center",
            boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
          }}
        >
          <h3 style={{ color: "#d9534f", marginBottom: "10px" }}>
            Overdue Communications
          </h3>
          <div
            style={{
              fontSize: "32px",
              color: "#d9534f",
              fontWeight: "bold",
            }}
          >
            {overdueTotal}
          </div>
        </div>

        {/* Due Today Card */}
        <div
          style={{
            flex: "1",
            backgroundColor: "#fff8e1",
            borderRadius: "10px",
            padding: "20px",
            textAlign: "center",
            boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
          }}
        >
          <h3 style={{ color: "#f0ad4e", marginBottom: "10px" }}>Due Today</h3>
          <div
            style={{
              fontSize: "32px",
              color: "#f0ad4e",
              fontWeight: "bold",
            }}
          >
            {dueTodayTotal}
          </div>
        </div>
      </div>

      <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
        {/* Overdue Section */}
        <div style={{ flex: "1", minWidth: "300px" }}>
          <h3 style={{ color: "#d9534f", marginBottom: "10px" }}>
            Overdue List
          </h3>
          {notificationsData.map((company, index) =>
            company.overdue.length > 0 ? (
              <div key={index} style={{ marginBottom: "20px" }}>
                {company.overdue.map((notification, idx) => (
                  <div
                    key={idx}
                    style={{
                      backgroundColor: "#fdecea",
                      padding: "15px",
                      borderRadius: "8px",
                      marginBottom: "10px",
                      boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
                    }}
                  >
                    <h4
                      style={{
                        color: "#337ab7",
                        marginBottom: "5px",
                        fontSize: "18px",
                      }}
                    >
                      {company.companyName}
                    </h4>
                    <p style={{ margin: "5px 0", color: "#333" }}>
                      {notification.description}
                    </p>
                    <p style={{ margin: "5px 0", color: "#555" }}>
                      Scheduled Date:{" "}
                      {new Date(
                        notification.scheduledDate
                      ).toLocaleDateString()}
                    </p>
                    <p style={{ margin: "5px 0", color: "#d9534f" }}>
                      {notification.daysOverdue} days overdue
                    </p>
                  </div>
                ))}
              </div>
            ) : (
              <p>No overdue notifications.</p>
            )
          )}
        </div>

        {/* Due Today Section */}
        {dueTodayTotal === 0 ? (
    <div
        style={{
            backgroundColor: "#fff8e1",
            padding: "15px",
            borderRadius: "8px",
            marginBottom: "10px",
            boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
            textAlign: "center",
        }}
    >
        <p style={{ margin: "5px 0", color: "#333" }}>
            No communications due today.
        </p>
    </div>
) : (
    notificationsData.map((company, index) =>
        company.dueToday.map((notification, idx) => (
            <div
                key={idx}
                style={{
                    backgroundColor: "#fff8e1",
                    padding: "15px",
                    borderRadius: "8px",
                    marginBottom: "10px",
                    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
                }}
            >
                <h4
                    style={{
                        color: "#337ab7",
                        marginBottom: "5px",
                        fontSize: "18px",
                    }}
                >
                    {company.companyName}
                </h4>
                <p style={{ margin: "5px 0", color: "#333" }}>
                    {notification.description}
                </p>
                <p style={{ margin: "5px 0", color: "#555" }}>
                    Scheduled Date:{" "}
                    {new Date(
                        notification.scheduledDate
                    ).toLocaleDateString()}
                </p>
            </div>
        ))
    )
)}

        </div>
      </div>
    </div>
    
  );
};

export default Notification;
