import React, { useEffect, useState } from "react";
import axios from "axios";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import config from "../../Common/Configurations/APIConfig";
import LayoutHeader from "../../Common/Layout/Header";
import LayoutSideBar from "../../Common/Layout/Sidebar";

// Set up moment localizer for react-big-calendar
const localizer = momentLocalizer(moment);

const MainCalendar = () => {
  const [communications, setCommunications] = useState([]);

  const headerconfig = {
    headers: {
      Authorization: "Bearer " + localStorage.getItem("userToken"),
      "Content-Type": "application/json",
    },
  };

  const apiConfig = {
    fetchCalendar: () =>
      axios.get(config.APIACTIVATEURL + config.GETCALENDAR, {
        ...headerconfig,
      }),
  };

  useEffect(() => {
    apiConfig
      .fetchCalendar()
      .then((response) => setCommunications(response.data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  // Transform API data into events for react-big-calendar
  const events = communications.map((comm) => ({
    title: `${comm.companyName} - ${comm.communicationType}`,
    start: new Date(comm.scheduledDate),
    end: new Date(comm.scheduledDate), // Assuming single-day events
  }));

  // Custom Styles
  const styles = {
    layoutWrapper: { backgroundColor: "#FAFAD2" },
    calendarContainer: {
      height: "80vh",
      margin: "20px",
      backgroundColor: "#F0F8FF",
      borderRadius: "10px",
      border: "2px solid #B0C4DE",
      padding: "10px",
      boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
    },
    todayCell: { backgroundColor: "#FFEFD5" },
    dayBgHover: { backgroundColor: "#E6E6FA" },
    toolbar: {
      backgroundColor: "#4682B4",
      color: "white",
      fontSize: "16px",
      padding: "10px",
      borderRadius: "5px",
      marginBottom: "10px",
      display: "flex",
      justifyContent: "space-between",
    },
    toolbarButton: {
      backgroundColor: "white",
      color: "#4682B4",
      border: "1px solid #4682B4",
      borderRadius: "5px",
      padding: "5px 10px",
      fontSize: "14px",
      cursor: "pointer",
      marginRight: "10px",
    },
    toolbarButtonActive: {
      backgroundColor: "#B0C4DE",
      color: "#4682B4",
    },
  };

  return (
    <div id="layout-wrapper" style={styles.layoutWrapper}>
      <LayoutHeader />
      <LayoutSideBar />
      <div style={{ padding: "120px 50px 0 50px" }}>
        <div style={styles.calendarContainer}>
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            style={{ height: "100%" }}
            views={["month", "week", "day", "agenda"]} // Ensuring views are added here
            defaultView="month"
            popup
            selectable
            components={{
              toolbar: (props) => (
                <div style={styles.toolbar}>
                  <button
                    onClick={() => props.onNavigate("PREV")}
                    style={styles.toolbarButton}
                  >
                    Prev
                  </button>
                  <span>{props.label}</span>
                  <button
                    onClick={() => props.onNavigate("NEXT")}
                    style={styles.toolbarButton}
                  >
                    Next
                  </button>
                </div>
              ),
            }}
            dayPropGetter={(date) => {
              const today = new Date();
              if (
                date.getDate() === today.getDate() &&
                date.getMonth() === today.getMonth() &&
                date.getFullYear() === today.getFullYear()
              ) {
                return { style: styles.todayCell };
              }
              return {
                style: {
                  backgroundColor: "#F8F8FF",
                  border: "1px solid #B0C4DE",
                },
                onMouseEnter: (e) =>
                  (e.target.style.backgroundColor = styles.dayBgHover.backgroundColor),
                onMouseLeave: (e) => (e.target.style.backgroundColor = "#F8F8FF"),
              };
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default MainCalendar;
